import React from "react"

import { IoIosArrowBack } from "react-icons/io"
import { IoIosArrowForward } from "react-icons/io"

function Educationals( { techArray }) {
    let displayedSlidesStart = 0
    let displayedSlidesEnd = 3

    const slideMoveForward = () => {
        if(displayedSlidesEnd < techArray.length) {
            displayedSlidesEnd++
            displayedSlidesStart++
        }
    }
    const slideMoveBackwards = () => {
        if(displayedSlidesStart > 0){
            displayedSlidesEnd--
            displayedSlidesStart--
        }
    }


    return (
        <section className="techBlurbsSection">
            <div className="sliderContainer">
                <IoIosArrowBack onClick={slideMoveBackwards}/>
                {
                    techArray.map((tech) => 
                    techArray.indexOf(tech) >= displayedSlidesStart && techArray.indexOf(tech) < displayedSlidesEnd ?
                        <div key={tech.name} className="activeBlurb">
                            <img src={tech.img} alt={tech.altText} className={tech.id + "Logo"}></img>
                            <h5>{tech.name}</h5>
                            <p>{tech.description}</p>
                            <input type={"button"} value="See Degrees" />
                        </div>
                        :
                        <div key={tech.name} className="blurb">
                            <img src={tech.img} alt={tech.altText} className={tech.id + "Logo"}></img>
                            <h5>{tech.name}</h5>
                            <p>{tech.description}</p>
                            <input type={"button"} value="See Degrees" />
                        </div>
                    )
                }
                <IoIosArrowForward onClick={slideMoveForward}/>
            </div>
        </section>
        )
}

export { Educationals }
